import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import * as Yup from 'yup';
import { Formik } from 'formik';
import { FaUser, FaLock, FaPhone, FaIdCard, FaCalendarAlt, FaVenusMars, FaBarcode, FaDownload, FaWhatsapp, FaHome, FaEnvelope, FaMapMarkerAlt, FaGraduationCap, FaCheckCircle, FaArrowLeft, FaArrowRight } from 'react-icons/fa';

// Custom TextField Component
const CustomTextField = ({ id, name, type = "text", label, value, onChange, error, touched, icon: Icon, isPassword, readOnly, placeholder, maxLength }) => {
  const [showPassword, setShowPassword] = useState(false);

  return (
    <div className="mb-4">
      <label htmlFor={id} className="block text-sm font-medium text-gray-700 mb-2">
        {label}
      </label>
      <div className="relative">
        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
          <Icon className="h-5 w-5 text-gray-400" />
        </div>
        <input
          id={id}
          name={name}
          type={isPassword ? (showPassword ? "text" : "password") : type}
          value={value}
          onChange={onChange}
          readOnly={readOnly}
          placeholder={placeholder}
          maxLength={maxLength}
          className={`
            block w-full pl-10 pr-10 py-3 border border-gray-300 rounded-lg
            focus:ring-2 focus:ring-blue-500 focus:border-blue-500
            transition-all duration-200 ease-in-out
            ${readOnly ? 'bg-gray-50 text-gray-600' : 'bg-white'}
            ${error && touched ? 'border-red-500 focus:ring-red-500 focus:border-red-500' : ''}
            backdrop-blur-sm bg-white/80
          `}
        />
        {isPassword && (
          <button
            type="button"
            className="absolute inset-y-0 right-0 pr-3 flex items-center"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? (
              <FaLock className="h-5 w-5 text-gray-400" />
            ) : (
              <FaLock className="h-5 w-5 text-gray-400" />
            )}
          </button>
        )}
      </div>
      {error && touched && (
        <p className="mt-1 text-sm text-red-600">{error}</p>
      )}
    </div>
  );
};

// Custom Button Component
const CustomButton = ({ children, type = "button", onClick, disabled, className = "" }) => {
  return (
    <button
      type={type}
      onClick={onClick}
      disabled={disabled}
      className={`
        w-full py-3 px-6 rounded-lg font-medium text-white
        bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800
        focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2
        transition-all duration-200 ease-in-out transform hover:scale-105
        disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none
        shadow-lg hover:shadow-xl
        ${className}
      `}
    >
      {children}
    </button>
  );
};

// Alert Box Component
const BasicAlertBox = ({ open, message, title, type, onConfirm }) => {
  if (!open) return null;

  const bgColor = {
    success: 'bg-green-50 border-green-200',
    danger: 'bg-red-50 border-red-200',
    warning: 'bg-yellow-50 border-yellow-200',
    info: 'bg-blue-50 border-blue-200'
  }[type] || 'bg-blue-50 border-blue-200';

  const textColor = {
    success: 'text-green-800',
    danger: 'text-red-800',
    warning: 'text-yellow-800',
    info: 'text-blue-800'
  }[type] || 'text-blue-800';

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className={`${bgColor} border rounded-lg p-6 max-w-md w-full backdrop-blur-sm bg-white/90`}>
        <h3 className={`text-lg font-semibold ${textColor} mb-2`}>{title}</h3>
        <p className={`${textColor} mb-4`}>{message}</p>
        <button
          onClick={onConfirm}
          className="w-full py-2 px-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          OK
        </button>
      </div>
    </div>
  );
};

// Validation schemas
const step1Schema = Yup.object().shape({
  firstName: Yup.string()
    .min(2, 'First name must be at least 2 characters')
    .required('First name is required'),
  lastName: Yup.string()
    .min(2, 'Last name must be at least 2 characters')
    .required('Last name is required'),
  email: Yup.string()
    .email('Invalid email format')
    .required('Email is required'),
  mobile: Yup.string()
    .matches(/^0\d{9}$/, 'Invalid phone number (should be 10 digits, start with 0)')
    .required('Mobile number is required'),
  password: Yup.string()
    .min(8, 'Password must be at least 8 characters')
    .matches(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/, 'Password must include uppercase, lowercase, and number')
    .required('Password is required'),
  confirmPassword: Yup.string()
    .oneOf([Yup.ref('password'), null], 'Passwords do not match')
    .required('Confirm password is required'),
});

const step2Schema = Yup.object().shape({
  address: Yup.string()
    .min(5, 'Address must be at least 5 characters')
    .required('Address is required'),
  city: Yup.string()
    .min(2, 'City must be at least 2 characters')
    .required('City is required'),
  serviceType: Yup.string()
    .required('Service type is required'),
  experience: Yup.string()
    .required('Experience level is required'),
});

export default function Register() {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [summaryValues, setSummaryValues] = useState({});
  const [registrationSuccess, setRegistrationSuccess] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [alertConfig, setAlertConfig] = useState({
    open: false,
    message: '',
    title: '',
    type: 'info',
    onConfirm: () => setAlertConfig(prev => ({ ...prev, open: false }))
  });

  const step1Values = {
    firstName: '',
    lastName: '',
    email: '',
    mobile: '',
    password: '',
    confirmPassword: '',
  };

  const step2Values = {
    address: '',
    city: '',
    serviceType: '',
    experience: '',
  };

  const serviceTypes = [
    'Cleaning', 'Plumbing', 'Electrical', 'Carpentry', 
    'Painting', 'Gardening', 'Moving', 'Repair', 'Other'
  ];

  const experienceLevels = [
    'Beginner (0-1 years)', 'Intermediate (2-5 years)', 
    'Experienced (5-10 years)', 'Expert (10+ years)'
  ];

  const handleStep1 = (values) => {
    setSummaryValues({ ...values });
    setStep(2);
  };

  const handleStep2 = (values) => {
    setSummaryValues({ ...summaryValues, ...values });
    setStep(3);
  };

  const handleRegister = async () => {
    setIsRegistering(true);

    try {
      const userData = {
        name: `${summaryValues.firstName} ${summaryValues.lastName}`,
        email: summaryValues.email,
        password: summaryValues.password,
        mobile: summaryValues.mobile,
        address: summaryValues.address,
        city: summaryValues.city,
        serviceType: summaryValues.serviceType,
        experience: summaryValues.experience,
        role: 'service_provider'
      };

      console.log('Sending registration data:', userData);

      const response = await fetch('http://Booking-Service-NLB-1ed6b29947aab9d7.elb.us-east-1.amazonaws.com:3000/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData),
      });

      const data = await response.json();

      if (data.token) {
        localStorage.setItem('token', data.token);
        setRegistrationSuccess(true);

        setAlertConfig({
          open: true,
          message: `Welcome to Home Service Platform! Your account has been successfully created.`,
          title: 'ðŸŽ‰ Registration Successful!',
          type: 'success',
          onConfirm: () => {
            setAlertConfig(prev => ({ ...prev, open: false }));
            navigate('/book');
          }
        });

        console.log('Registration successful!');
      } else {
        console.error('Registration failed:', data.message);
        setAlertConfig({
          open: true,
          message: 'Registration failed: ' + (data.message || 'Unknown error'),
          title: 'Registration Failed',
          type: 'danger',
          onConfirm: () => setAlertConfig(prev => ({ ...prev, open: false }))
        });
      }
    } catch (error) {
      console.error('Registration error:', error);
      setAlertConfig({
        open: true,
        message: 'Registration failed: Network error occurred',
        title: 'Registration Failed',
        type: 'danger',
        onConfirm: () => setAlertConfig(prev => ({ ...prev, open: false }))
      });
    } finally {
      setIsRegistering(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 flex items-center justify-center p-4">
      <BasicAlertBox {...alertConfig} />

      <div className="w-full max-w-md">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="w-16 h-16 rounded-full bg-gradient-to-r from-blue-600 to-purple-600 flex items-center justify-center mx-auto mb-4 shadow-xl backdrop-blur-sm">
            <FaHome className="text-white text-2xl" />
          </div>
          <h1 className="text-3xl font-bold text-gray-800 mb-2">
            Home Service
          </h1>
          <p className="text-gray-600 font-medium">
            Service Provider Registration
          </p>
        </div>

        {/* Progress Bar */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-2">
            <span className="text-sm font-medium text-gray-700">Step {step} of 3</span>
            <span className="text-sm text-gray-500">{Math.round((step / 3) * 100)}%</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2">
            <div 
              className="bg-gradient-to-r from-blue-600 to-purple-600 h-2 rounded-full transition-all duration-300"
              style={{ width: `${(step / 3) * 100}%` }}
            ></div>
          </div>
        </div>

        {/* Glassmorphism Card */}
        <div className="backdrop-blur-lg bg-white/30 rounded-2xl shadow-2xl border border-white/20 p-8">
          {step === 1 && (
            <Formik
              initialValues={step1Values}
              validationSchema={step1Schema}
              onSubmit={handleStep1}
            >
              {({ errors, touched, handleChange, values, handleSubmit }) => (
                <form onSubmit={handleSubmit} className="space-y-4">
                  <h2 className="text-xl font-bold text-gray-800 mb-6 text-center">
                    Personal Information
                  </h2>

                  <CustomTextField
                    id="firstName"
                    name="firstName"
                    label="First Name *"
                    value={values.firstName}
                    onChange={handleChange}
                    error={errors.firstName}
                    touched={touched.firstName}
                    icon={FaUser}
                    placeholder="Enter your first name"
                  />

                  <CustomTextField
                    id="lastName"
                    name="lastName"
                    label="Last Name *"
                    value={values.lastName}
                    onChange={handleChange}
                    error={errors.lastName}
                    touched={touched.lastName}
                    icon={FaUser}
                    placeholder="Enter your last name"
                  />

                  <CustomTextField
                    id="email"
                    name="email"
                    type="email"
                    label="Email Address *"
                    value={values.email}
                    onChange={handleChange}
                    error={errors.email}
                    touched={touched.email}
                    icon={FaEnvelope}
                    placeholder="Enter your email"
                  />

                  <CustomTextField
                    id="mobile"
                    name="mobile"
                    label="Mobile Number *"
                    value={values.mobile}
                    onChange={handleChange}
                    error={errors.mobile}
                    touched={touched.mobile}
                    icon={FaPhone}
                    placeholder="0XXXXXXXXX"
                  />

                  <CustomTextField
                    id="password"
                    name="password"
                    label="Password *"
                    value={values.password}
                    onChange={handleChange}
                    error={errors.password}
                    touched={touched.password}
                    isPassword
                    icon={FaLock}
                    placeholder="Create a strong password"
                  />

                  <CustomTextField
                    id="confirmPassword"
                    name="confirmPassword"
                    label="Confirm Password *"
                    value={values.confirmPassword}
                    onChange={handleChange}
                    error={errors.confirmPassword}
                    touched={touched.confirmPassword}
                    isPassword
                    icon={FaLock}
                    placeholder="Confirm your password"
                  />

                  <div className="flex gap-4 mt-6">
                    <CustomButton 
                      type="button" 
                      onClick={() => navigate('/login')}
                      className="bg-gray-500 hover:bg-gray-600"
                    >
                      <FaArrowLeft className="inline mr-2" />
                      Back to Login
                    </CustomButton>
                    <CustomButton type="submit">
                      Next Step
                      <FaArrowRight className="inline ml-2" />
                    </CustomButton>
                  </div>
                </form>
              )}
            </Formik>
          )}

          {step === 2 && (
            <Formik
              initialValues={step2Values}
              validationSchema={step2Schema}
              onSubmit={handleStep2}
            >
              {({ errors, touched, handleChange, values, handleSubmit }) => (
                <form onSubmit={handleSubmit} className="space-y-4">
                  <h2 className="text-xl font-bold text-gray-800 mb-6 text-center">
                    Service Details
                  </h2>

                  <CustomTextField
                    id="address"
                    name="address"
                    label="Address *"
                    value={values.address}
                    onChange={handleChange}
                    error={errors.address}
                    touched={touched.address}
                    icon={FaMapMarkerAlt}
                    placeholder="Enter your full address"
                  />

                  <CustomTextField
                    id="city"
                    name="city"
                    label="City *"
                    value={values.city}
                    onChange={handleChange}
                    error={errors.city}
                    touched={touched.city}
                    icon={FaMapMarkerAlt}
                    placeholder="Enter your city"
                  />

                  <div className="mb-4">
                    <label htmlFor="serviceType" className="block text-sm font-medium text-gray-700 mb-2">
                      Service Type *
                    </label>
                    <div className="relative">
                      <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                        <FaGraduationCap className="h-5 w-5 text-gray-400" />
                      </div>
                      <select
                        id="serviceType"
                        name="serviceType"
                        value={values.serviceType}
                        onChange={handleChange}
                        className="block w-full pl-10 pr-10 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 ease-in-out backdrop-blur-sm bg-white/80"
                      >
                        <option value="">Select Service Type</option>
                        {serviceTypes.map(type => (
                          <option key={type} value={type}>{type}</option>
                        ))}
                      </select>
                    </div>
                    {errors.serviceType && touched.serviceType && (
                      <p className="mt-1 text-sm text-red-600">{errors.serviceType}</p>
                    )}
                  </div>

                  <div className="mb-4">
                    <label htmlFor="experience" className="block text-sm font-medium text-gray-700 mb-2">
                      Experience Level *
                    </label>
                    <div className="relative">
                      <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                        <FaGraduationCap className="h-5 w-5 text-gray-400" />
                      </div>
                      <select
                        id="experience"
                        name="experience"
                        value={values.experience}
                        onChange={handleChange}
                        className="block w-full pl-10 pr-10 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-200 ease-in-out backdrop-blur-sm bg-white/80"
                      >
                        <option value="">Select Experience Level</option>
                        {experienceLevels.map(level => (
                          <option key={level} value={level}>{level}</option>
                        ))}
                      </select>
                    </div>
                    {errors.experience && touched.experience && (
                      <p className="mt-1 text-sm text-red-600">{errors.experience}</p>
                    )}
                  </div>

                  <div className="flex gap-4 mt-6">
                    <CustomButton 
                      type="button" 
                      onClick={() => setStep(1)}
                      className="bg-gray-500 hover:bg-gray-600"
                    >
                      <FaArrowLeft className="inline mr-2" />
                      Previous
                    </CustomButton>
                    <CustomButton type="submit">
                      Next Step
                      <FaArrowRight className="inline ml-2" />
                    </CustomButton>
                  </div>
                </form>
              )}
            </Formik>
          )}

          {step === 3 && (
            <div className="space-y-4">
              {!registrationSuccess ? (
                <>
                  <h2 className="text-xl font-bold text-gray-800 mb-6 text-center">
                    Review & Complete
                  </h2>

                  <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
                    <div className="flex items-center gap-2 mb-2">
                      <FaCheckCircle className="text-blue-600 text-lg" />
                      <span className="font-semibold text-blue-800">Review Your Information</span>
                    </div>
                    <p className="text-sm text-blue-700">
                      Please review all your details before completing registration.
                    </p>
                  </div>

                  <div className="space-y-3">
                    <CustomTextField label="Full Name" value={`${summaryValues.firstName} ${summaryValues.lastName}`} readOnly icon={FaUser} />
                    <CustomTextField label="Email" value={summaryValues.email} readOnly icon={FaEnvelope} />
                    <CustomTextField label="Mobile" value={summaryValues.mobile} readOnly icon={FaPhone} />
                    <CustomTextField label="Address" value={summaryValues.address} readOnly icon={FaMapMarkerAlt} />
                    <CustomTextField label="City" value={summaryValues.city} readOnly icon={FaMapMarkerAlt} />
                    <CustomTextField label="Service Type" value={summaryValues.serviceType} readOnly icon={FaGraduationCap} />
                    <CustomTextField label="Experience" value={summaryValues.experience} readOnly icon={FaGraduationCap} />
                  </div>

                  <div className="bg-green-50 border border-green-200 rounded-lg p-4 mt-6">
                    <p className="text-sm text-green-700 font-medium">
                      By clicking "Complete Registration", you agree to our terms and conditions.
                    </p>
                  </div>

                  <div className="flex gap-4 mt-6">
                    <CustomButton 
                      type="button" 
                      onClick={() => setStep(2)}
                      className="bg-gray-500 hover:bg-gray-600"
                    >
                      <FaArrowLeft className="inline mr-2" />
                      Previous
                    </CustomButton>
                    <CustomButton 
                      type="button" 
                      onClick={handleRegister}
                      disabled={isRegistering}
                    >
                      {isRegistering ? (
                        <>
                          <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin inline mr-2"></div>
                          Registering...
                        </>
                      ) : (
                        <>
                          Complete Registration
                          <FaCheckCircle className="inline ml-2" />
                        </>
                      )}
                    </CustomButton>
                  </div>
                </>
              ) : (
                <div className="text-center">
                  <div className="w-20 h-20 bg-gradient-to-r from-green-400 to-green-600 rounded-full flex items-center justify-center mx-auto mb-6 shadow-lg">
                    <FaCheckCircle className="w-10 h-10 text-white" />
                  </div>

                  <div className="bg-gradient-to-r from-green-50 to-blue-50 border border-green-200 rounded-xl p-6 mb-6 shadow-sm">
                    <h3 className="text-2xl font-bold text-green-800 mb-3">ðŸŽ‰ Registration Successful!</h3>
                    <p className="text-lg text-gray-700 mb-4">
                      Welcome to <span className="font-bold text-blue-600">Home Service Platform</span>
                    </p>

                    <div className="bg-white rounded-lg p-4 mb-4 border border-gray-200">
                      <div className="grid grid-cols-1 gap-4 text-left">
                        <div>
                          <p className="text-sm text-gray-600 mb-1">Full Name</p>
                          <p className="text-lg font-semibold text-gray-800">{summaryValues.firstName} {summaryValues.lastName}</p>
                        </div>
                        <div>
                          <p className="text-sm text-gray-600 mb-1">Service Type</p>
                          <p className="text-lg font-semibold text-blue-600">{summaryValues.serviceType}</p>
                        </div>
                      </div>
                    </div>

                    <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                      <div className="flex items-center gap-2 mb-2">
                        <FaCheckCircle className="w-5 h-5 text-blue-600" />
                        <span className="font-semibold text-blue-800">Account Status</span>
                      </div>
                      <p className="text-sm text-blue-700">
                        âœ… Account Created | âœ… Ready to Provide Services
                      </p>
                    </div>
                  </div>

                  <CustomButton
                    onClick={() => navigate('/book')}
                    className="w-full"
                  >
                    Start Providing Services
                  </CustomButton>
                </div>
              )}
            </div>
          )}
        </div>

        <div className="text-center mt-8">
          <Link to="/login" className="text-blue-600 hover:text-blue-800 text-sm font-medium transition-colors">
            Already have an account? Sign in
          </Link>
        </div>
      </div>
    </div>
  );
}
