import React from 'react';
import { isDemoMode } from '../config';

const DemoIndicator = () => {
  if (!isDemoMode()) return null;

  return (
    <div className="fixed top-4 right-4 z-50">
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-4 py-2 rounded-full shadow-lg border border-white/20 backdrop-blur-sm">
        <div className="flex items-center gap-2">
          <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
          <span className="text-sm font-semibold">DEMO MODE</span>
          <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
          </svg>
        </div>
      </div>

      {/* Tooltip */}
      <div className="absolute top-full right-0 mt-2 w-64 bg-black/90 text-white text-xs rounded-lg p-3 shadow-xl border border-white/10 backdrop-blur-sm opacity-0 group-hover:opacity-100 transition-opacity duration-200">
        <p className="mb-2 font-semibold">Demo Mode Active</p>
        <p className="text-gray-300">
          This application is running in demo mode using localStorage. 
          All data is stored locally in your browser.
        </p>
        <div className="mt-2 pt-2 border-t border-white/20">
          <p className="text-blue-300 font-mono text-xs">
            API: localStorage
          </p>
        </div>
      </div>
    </div>
  );
};

export default DemoIndicator;
