// Configuration for Home Service App
// Switch between demo mode (localStorage) and live mode (AWS API Gateway)

const config = {
  // Set to 'demo' for localStorage or 'live' for AWS API Gateway
  mode: 'demo', // Change this to 'live' to use AWS API Gateway

  // API Configuration
  api: {
    // Demo mode - uses localStorage
    demo: {
      baseURL: null,
      useLocalStorage: true
    },

    // Live mode - uses AWS API Gateway
    live: {
      baseURL: 'http://Booking-Service-NLB-1ed6b29947aab9d7.elb.us-east-1.amazonaws.com:3000',
      useLocalStorage: false,
      endpoints: {
        register: '/auth/register',
        login: '/auth/login',
        book: '/api/book',
        bookings: '/api/bookings'
      }
    }
  },

  // Demo data for localStorage mode
  demoData: {
    services: [
      { id: "plumber", name: "Plumber", icon: "ðŸ”§", description: "Pipe repairs, installations, maintenance" },
      { id: "electrician", name: "Electrician", icon: "âš¡", description: "Electrical repairs, installations, wiring" },
      { id: "cleaner", name: "House Cleaner", icon: "ðŸ§¹", description: "Regular cleaning, deep cleaning, maintenance" },
      { id: "gardener", name: "Gardener", icon: "ðŸŒ¿", description: "Lawn care, landscaping, plant maintenance" },
      { id: "painter", name: "Painter", icon: "ðŸŽ¨", description: "Interior/exterior painting, touch-ups" },
      { id: "carpenter", name: "Carpenter", icon: "ðŸ”¨", description: "Furniture repair, installations, woodwork" },
      { id: "other", name: "Other Service", icon: "ðŸ”§", description: "Custom service not listed above" }
    ]
  }
};

// Helper functions
export const getConfig = () => {
  return config.mode === 'live' ? config.api.live : config.api.demo;
};

export const isDemoMode = () => {
  return config.mode === 'demo';
};

export const isLiveMode = () => {
  return config.mode === 'live';
};

export const getApiUrl = (endpoint) => {
  const apiConfig = getConfig();
  if (apiConfig.useLocalStorage) {
    return null; // No API calls in demo mode
  }
  return `${apiConfig.baseURL}${apiConfig.endpoints[endpoint] || ''}`;
};

export default config;
