import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';

export default function Register() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [showConfirmPassword, setShowConfirmPassword] = useState(false);
    const navigate = useNavigate();

    const handleRegister = async (e) => {
        e.preventDefault();
        setLoading(true);
        const res = await fetch('http://Booking-Service-NLB-1ed6b29947aab9d7.elb.us-east-1.amazonaws.com:3000/auth/register', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ name, email, password }),
        });
        const data = await res.json();
        setLoading(false);
        if (data.token) {
            localStorage.setItem('token', data.token);
            navigate('/book');
        } else {
            alert('Registration failed!');
        }
    };

    return (
            <div className="flex items-center justify-center min-h-[70vh] bg-gradient-to-br from-white via-slate-100 to-slate-200">
                <div className="w-full max-w-md bg-white/60 backdrop-blur-2xl rounded-3xl shadow-2xl p-10 border border-slate-100 ring-1 ring-blue-200/60" style={{boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.08)'}}>
            <h2 className="text-3xl font-extrabold text-center bg-gradient-to-r from-blue-900 via-blue-700 to-blue-400 bg-clip-text text-transparent mb-8 drop-shadow">Create Account</h2>
                <form onSubmit={handleRegister} className="space-y-6">
                                                                    <div>
                                                                        <label className="block text-slate-800 font-semibold mb-1" htmlFor="name">Name</label>
                                                                        <input
                                                                            id="name"
                                                                            type="text"
                                                                            value={name}
                                                                            onChange={(e) => setName(e.target.value)}
                                                                            placeholder="Your Name"
                                                                            required
                                                                            className="w-full px-4 py-2 border border-slate-200 bg-white/80 backdrop-blur-md rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-200 focus:border-blue-400 transition shadow-sm placeholder-slate-400"
                                                                        />
                                                                    </div>
                                                                    <div>
                                                                        <label className="block text-slate-800 font-semibold mb-1" htmlFor="email">Email</label>
                                                                        <input
                                                                            id="email"
                                                                            type="email"
                                                                            value={email}
                                                                            onChange={(e) => setEmail(e.target.value)}
                                                                            placeholder="you@email.com"
                                                                            required
                                                                            className="w-full px-4 py-2 border border-slate-200 bg-white/80 backdrop-blur-md rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-200 focus:border-blue-400 transition shadow-sm placeholder-slate-400"
                                                                        />
                                                                    </div>
                                                                    <div>
                                                                        <label className="block text-slate-800 font-semibold mb-1" htmlFor="password">Password</label>
                                                                        <input
                                                                            id="password"
                                                                            type="password"
                                                                            value={password}
                                                                            onChange={(e) => setPassword(e.target.value)}
                                                                            placeholder="Password"
                                                                            required
                                                                            className="w-full px-4 py-2 border border-slate-200 bg-white/80 backdrop-blur-md rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-200 focus:border-blue-400 transition shadow-sm placeholder-slate-400"
                                                                        />
                                                                    </div>
                                                        <button
                                                            type="submit"
                                                            className="w-full py-2 px-4 bg-gradient-to-r from-blue-900 via-blue-700 to-blue-500 text-white font-bold rounded-xl shadow-lg hover:from-blue-800 hover:to-blue-600 transition disabled:opacity-60"
                                                            disabled={loading}
                                                        >
                                                            {loading ? 'Registering...' : 'Register'}
                                                        </button>
                </form>
                                        <p className="mt-8 text-center text-slate-500 text-sm">
                                            Already have an account?{' '}
                                            <a href="/login" className="text-blue-800 hover:underline font-semibold">Login</a>
                                        </p>
            </div>
        </div>
    );
}
