import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function Booking() {
    const [name, setName] = useState("");
    const [service, setService] = useState("");
    const [datetime, setDatetime] = useState("");
    const [message, setMessage] = useState("");
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [selectedDate, setSelectedDate] = useState("");
    const [selectedTime, setSelectedTime] = useState("");
    const navigate = useNavigate();

    // Predefined service options
    const serviceOptions = [
        { id: "plumber", name: "Plumber", icon: "ðŸ”§", description: "Pipe repairs, installations, maintenance" },
        { id: "electrician", name: "Electrician", icon: "âš¡", description: "Electrical repairs, installations, wiring" },
        { id: "cleaner", name: "House Cleaner", icon: "ðŸ§¹", description: "Regular cleaning, deep cleaning, maintenance" },
        { id: "gardener", name: "Gardener", icon: "ðŸŒ¿", description: "Lawn care, landscaping, plant maintenance" },
        { id: "painter", name: "Painter", icon: "ðŸŽ¨", description: "Interior/exterior painting, touch-ups" },
        { id: "carpenter", name: "Carpenter", icon: "ðŸ”¨", description: "Furniture repair, installations, woodwork" },
        { id: "other", name: "Other Service", icon: "ðŸ”§", description: "Custom service not listed above" }
    ];

    useEffect(() => {
        const token = localStorage.getItem("token");
        // if (!token) navigate("/login");
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);
        setMessage("");

        // Combine date and time
        const combinedDateTime = selectedDate && selectedTime ? `${selectedDate}T${selectedTime}` : datetime;

        const token = localStorage.getItem("token");
        try {
            const res = await fetch(
                "https://1xxcc82bs9.execute-api.us-east-1.amazonaws.com/v1/api/book",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`,
                    },
                    body: JSON.stringify({ name, service, datetime: combinedDateTime }),
                }
            );

            const data = await res.json();

            if (!res.ok) {
                setMessage(data.error || "Booking failed");
            } else {
                setMessage(data.message);
                setName("");
                setService("");
                setDatetime("");
                // Set flag to show success message on booking requests page
                sessionStorage.setItem('justBooked', 'true');
                // Redirect to booking requests page after successful booking
                setTimeout(() => {
                    navigate("/requests");
                }, 1500);
            }
        } catch (error) {
            setMessage("Something went wrong. Please try again later.");
            console.error(error);
        } finally {
            setIsSubmitting(false);
        }
    };

        return (
            <div className="flex items-center justify-center min-h-[90vh] bg-gradient-to-br from-white via-slate-100 to-slate-200 py-8">
                <div className="w-full max-w-5xl flex flex-col md:flex-row items-stretch gap-0 md:gap-12 bg-white/50 backdrop-blur-2xl rounded-3xl shadow-2xl p-0 md:p-12 border border-slate-100 ring-1 ring-blue-200/60" style={{boxShadow: '0 12px 48px 0 rgba(31, 38, 135, 0.13)'}}>
                    {/* Illustration/Side Panel */}
                    <div className="hidden md:flex flex-col justify-center items-center w-1/2 h-full bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 rounded-l-3xl p-12 text-white select-none relative overflow-hidden">
                        <svg width="140" height="140" fill="none" viewBox="0 0 24 24" className="mb-8 drop-shadow-xl"><path d="M12 3L2 9l10 6 10-6-10-6zm0 6.75L4.21 9 12 13.5 19.79 9 12 9.75zm0 2.25l-7.79-4.5V17a2 2 0 002 2h11.58a2 2 0 002-2V7.5L12 12z" fill="currentColor"/></svg>
                        <h2 className="text-3xl font-black mb-3 tracking-tight drop-shadow">Book Your Service</h2>
                        <p className="text-lg opacity-90 text-center leading-relaxed">Fast, reliable, and professional home services at your fingertips.<br/>Schedule your appointment with ease.</p>
                        <div className="absolute bottom-6 left-0 w-full flex justify-center opacity-30 pointer-events-none">
                            <svg width="180" height="40" viewBox="0 0 180 40" fill="none"><ellipse cx="90" cy="20" rx="90" ry="20" fill="white"/></svg>
                        </div>
                    </div>
                    {/* Booking Form */}
                    <div className="w-full md:w-1/2 p-8 md:p-0 flex flex-col justify-center">
                        {/* Stepper/Header */}
                        <div className="flex items-center justify-center gap-4 mb-8">
                            <div className="flex items-center gap-2">
                                <span className="w-3 h-3 rounded-full bg-blue-700 border-2 border-white shadow"></span>
                                <span className="text-blue-900 font-bold">Details</span>
                            </div>
                            <span className="w-8 h-0.5 bg-blue-200 rounded"></span>
                            <div className="flex items-center gap-2 opacity-60">
                                <span className="w-3 h-3 rounded-full bg-blue-200 border-2 border-white"></span>
                                <span className="text-blue-400 font-semibold">Confirm</span>
                            </div>
                        </div>
                        <h1 className="text-3xl font-extrabold text-center bg-gradient-to-r from-blue-900 via-blue-700 to-blue-400 bg-clip-text text-transparent mb-8 drop-shadow">Book a Service</h1>
                        <form onSubmit={handleSubmit} className="space-y-8">
                            {/* Floating label effect */}
                            <div className="relative">
                                <input
                                    id="name"
                                    type="text"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    placeholder=" "
                                    required
                                    className="peer w-full px-4 py-3 border border-slate-200 bg-white/80 backdrop-blur-md rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-200 focus:border-blue-400 transition shadow-sm placeholder-transparent"
                                />
                                <label htmlFor="name" className="absolute left-4 top-3 text-slate-500 font-medium transition-all duration-200 pointer-events-none peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-4 peer-focus:text-xs peer-focus:text-blue-700 peer-valid:-top-4 peer-valid:text-xs peer-valid:text-blue-700 bg-white/80 px-1 rounded">Name</label>
                            </div>
                            <div className="relative">
                                <input
                                    id="service"
                                    type="text"
                                    value={service}
                                    onChange={(e) => setService(e.target.value)}
                                    placeholder=" "
                                    required
                                    className="peer w-full px-4 py-3 border border-slate-200 bg-white/80 backdrop-blur-md rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-200 focus:border-blue-400 transition shadow-sm placeholder-transparent"
                                />
                                <label htmlFor="service" className="absolute left-4 top-3 text-slate-500 font-medium transition-all duration-200 pointer-events-none peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-4 peer-focus:text-xs peer-focus:text-blue-700 peer-valid:-top-4 peer-valid:text-xs peer-valid:text-blue-700 bg-white/80 px-1 rounded">Service (e.g., Plumber)</label>
                            </div>
                            <div className="relative">
                                <input
                                    id="datetime"
                                    type="datetime-local"
                                    value={datetime}
                                    onChange={(e) => setDatetime(e.target.value)}
                                    required
                                    className="peer w-full px-4 py-3 border border-slate-200 bg-white/80 backdrop-blur-md rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-200 focus:border-blue-400 transition shadow-sm placeholder-transparent"
                                />
                                <label htmlFor="datetime" className="absolute left-4 top-3 text-slate-500 font-medium transition-all duration-200 pointer-events-none peer-placeholder-shown:top-3 peer-placeholder-shown:text-base peer-focus:-top-4 peer-focus:text-xs peer-focus:text-blue-700 peer-valid:-top-4 peer-valid:text-xs peer-valid:text-blue-700 bg-white/80 px-1 rounded">Date & Time</label>
                            </div>
                            <button
                                type="submit"
                                disabled={isSubmitting}
                                className="w-full py-3 px-4 flex items-center justify-center gap-2 bg-gradient-to-r from-blue-900 via-blue-700 to-blue-500 text-white font-bold rounded-xl shadow-xl hover:from-blue-800 hover:to-blue-600 transition disabled:opacity-60 text-lg"
                            >
                                {isSubmitting ? (
                                    <>
                                        <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                        </svg>
                                        Booking...
                                    </>
                                ) : (
                                    <>
                                        <svg className="w-5 h-5" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" d="M12 4v16m8-8H4"/></svg>
                                        Book Now
                                    </>
                                )}
                            </button>
                        </form>
                        {message && (
                            <div className="mt-8 text-center">
                                <p className="text-base font-semibold text-green-600 mb-2">
                                    {message}
                                </p>
                                {message.includes("successfully") && (
                                    <p className="text-sm text-blue-600">
                                        Redirecting to booking requests...
                                    </p>
                                )}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        );
}
