// Configuration file for API endpoints and backend URLs
export const API_CONFIG = {
  // Backend service URL
  BASE_URL: 'http://bookingservice-alb-1975395896.us-east-1.elb.amazonaws.com',

  // API endpoints
  ENDPOINTS: {
    // Authentication endpoints
    REGISTER: '/auth/register',
    LOGIN: '/auth/login',

    // Booking endpoints
    BOOK: '/api/book',
    BOOKINGS: '/api/bookings',

    // Health check
    HEALTH: '/health'
  }
};

// Helper function to get full API URL
export const getApiUrl = (endpoint) => {
  return `${API_CONFIG.BASE_URL}${endpoint}`;
};

// Helper function to get endpoint URL
export const getEndpoint = (endpointName) => {
  return getApiUrl(API_CONFIG.ENDPOINTS[endpointName]);
};

export default API_CONFIG;
