import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate, useLocation } from "react-router-dom";
import Login from "./Login";
import Register from "./Register";
import Booking from "./Booking";
import BookingRequest from "./BookingRequest";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsAuthenticated(true);
      // You can decode the token to get user info if needed
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setUser(payload);
      } catch (error) {
        console.error('Error parsing token:', error);
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsAuthenticated(false);
    setUser(null);
    setIsMobileMenuOpen(false);
  };
  return (
      <div className="min-h-screen bg-gray-50">
        <Router>
          {/* Navbar */}
          <nav className="bg-white/30 backdrop-blur-2xl border-b border-slate-100 shadow-2xl sticky top-0 z-40" style={{boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.10)'}}>
            <div className="max-w-6xl mx-auto px-4 sm:px-6 py-3">
              <div className="flex justify-between items-center">
                {/* Logo */}
                <div className="flex items-center gap-3">
                  <Link to="/" className="flex items-center gap-3 hover:opacity-80 transition">
                    <span className="inline-flex items-center justify-center w-10 h-10 bg-gradient-to-tr from-blue-900 via-blue-600 to-blue-300 rounded-2xl shadow-lg border-2 border-white/60">
                      <svg width="24" height="24" fill="none" viewBox="0 0 24 24" className="text-white">
                        <path d="M12 3L2 9l10 6 10-6-10-6zm0 6.75L4.21 9 12 13.5 19.79 9 12 9.75zm0 2.25l-7.79-4.5V17a2 2 0 002 2h11.58a2 2 0 002-2V7.5L12 12z" fill="currentColor"/>
                      </svg>
                    </span>
                    <h1 className="text-xl sm:text-2xl font-black bg-gradient-to-r from-blue-900 via-blue-700 to-blue-400 bg-clip-text text-transparent tracking-tight drop-shadow-sm select-none">
                      Home Service
                    </h1>
                  </Link>
                </div>

                {/* Desktop Navigation */}
                <div className="hidden md:flex gap-2 lg:gap-6 items-center">
                  {!isAuthenticated ? (
                    <>
                      <Link
                        to="/register"
                        className="px-4 py-2 rounded-lg font-semibold text-slate-700 hover:text-blue-900 hover:bg-blue-50/80 transition shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-200"
                      >
                        Register
                      </Link>
                      <Link
                        to="/login"
                        className="px-4 py-2 rounded-lg font-semibold text-slate-700 hover:text-blue-900 hover:bg-blue-50/80 transition shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-200"
                      >
                        Login
                      </Link>
                    </>
                  ) : (
                    <>
                      <Link
                        to="/requests"
                        className="px-4 py-2 rounded-lg font-semibold text-slate-700 hover:text-blue-900 hover:bg-blue-50/80 transition shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-200"
                      >
                        My Requests
                      </Link>
                      <div className="flex items-center gap-2 px-3 py-1 bg-blue-50 rounded-lg">
                        <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                        <span className="text-sm font-medium text-blue-900">
                          {user?.email ? user.email.split('@')[0] : 'User'}
                        </span>
                      </div>
                      <button
                        onClick={handleLogout}
                        className="px-4 py-2 rounded-lg font-semibold text-red-600 hover:text-red-700 hover:bg-red-50/80 transition shadow-sm focus:outline-none focus:ring-2 focus:ring-red-200"
                      >
                        Logout
                      </button>
                    </>
                  )}
                  <Link
                    to="/book"
                    className="px-5 py-2 rounded-2xl font-bold bg-gradient-to-r from-blue-900 via-blue-700 to-blue-500 text-white shadow-xl hover:from-blue-800 hover:to-blue-600 transition focus:outline-none focus:ring-2 focus:ring-blue-300"
                  >
                    Book Now
                  </Link>
                </div>

                {/* Mobile menu button */}
                <div className="md:hidden">
                  <button
                    onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                    className="p-2 rounded-lg text-slate-700 hover:text-blue-900 hover:bg-blue-50/80 transition focus:outline-none focus:ring-2 focus:ring-blue-200"
                  >
                    <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      {isMobileMenuOpen ? (
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                      ) : (
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
                      )}
                    </svg>
                  </button>
                </div>
              </div>

              {/* Mobile Navigation */}
              {isMobileMenuOpen && (
                <div className="md:hidden mt-4 pb-4 border-t border-slate-200">
                  <div className="flex flex-col gap-2 pt-4">
                    {!isAuthenticated ? (
                      <>
                        <Link
                          to="/register"
                          onClick={() => setIsMobileMenuOpen(false)}
                          className="px-4 py-2 rounded-lg font-semibold text-slate-700 hover:text-blue-900 hover:bg-blue-50/80 transition"
                        >
                          Register
                        </Link>
                        <Link
                          to="/login"
                          onClick={() => setIsMobileMenuOpen(false)}
                          className="px-4 py-2 rounded-lg font-semibold text-slate-700 hover:text-blue-900 hover:bg-blue-50/80 transition"
                        >
                          Login
                        </Link>
                      </>
                    ) : (
                      <>
                        <Link
                          to="/requests"
                          onClick={() => setIsMobileMenuOpen(false)}
                          className="px-4 py-2 rounded-lg font-semibold text-slate-700 hover:text-blue-900 hover:bg-blue-50/80 transition"
                        >
                          My Requests
                        </Link>
                        <div className="px-4 py-2 flex items-center gap-2">
                          <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                          <span className="text-sm font-medium text-blue-900">
                            {user?.email ? user.email.split('@')[0] : 'User'}
                          </span>
                        </div>
                        <button
                          onClick={() => {
                            handleLogout();
                            setIsMobileMenuOpen(false);
                          }}
                          className="px-4 py-2 rounded-lg font-semibold text-red-600 hover:text-red-700 hover:bg-red-50/80 transition text-left"
                        >
                          Logout
                        </button>
                      </>
                    )}
                    <Link
                      to="/book"
                      onClick={() => setIsMobileMenuOpen(false)}
                      className="px-4 py-2 rounded-lg font-bold bg-gradient-to-r from-blue-900 via-blue-700 to-blue-500 text-white shadow-lg hover:from-blue-800 hover:to-blue-600 transition"
                    >
                      Book Now
                    </Link>
                  </div>
                </div>
              )}
            </div>
          </nav>

          {/* Routes */}
          <div className="max-w-6xl mx-auto px-4 sm:px-6 py-8">
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/register" element={<Register />} />
              <Route path="/login" element={<Login />} />
              <Route path="/book" element={<Booking />} />
              <Route path="/requests" element={<BookingRequest />} />
            </Routes>
          </div>
        </Router>
      </div>
  );
}

export default App;
