import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function Booking() {
    const [name, setName] = useState("");
    const [service, setService] = useState("");
    const [datetime, setDatetime] = useState("");
    const [message, setMessage] = useState("");
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [selectedDate, setSelectedDate] = useState("");
    const [selectedTime, setSelectedTime] = useState("");
    const navigate = useNavigate();

    // Predefined service options
    const serviceOptions = [
        { id: "plumber", name: "Plumber", icon: "ðŸ”§", description: "Pipe repairs, installations, maintenance" },
        { id: "electrician", name: "Electrician", icon: "âš¡", description: "Electrical repairs, installations, wiring" },
        { id: "cleaner", name: "House Cleaner", icon: "ðŸ§¹", description: "Regular cleaning, deep cleaning, maintenance" },
        { id: "gardener", name: "Gardener", icon: "ðŸŒ¿", description: "Lawn care, landscaping, plant maintenance" },
        { id: "painter", name: "Painter", icon: "ðŸŽ¨", description: "Interior/exterior painting, touch-ups" },
        { id: "carpenter", name: "Carpenter", icon: "ðŸ”¨", description: "Furniture repair, installations, woodwork" },
        { id: "other", name: "Other Service", icon: "ðŸ”§", description: "Custom service not listed above" }
    ];

    useEffect(() => {
        const token = localStorage.getItem("token");
        // if (!token) navigate("/login");
    }, [navigate]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsSubmitting(true);
        setMessage("");

        // Combine date and time
        const combinedDateTime = selectedDate && selectedTime ? `${selectedDate}T${selectedTime}` : datetime;

        const token = localStorage.getItem("token");
        try {
            const res = await fetch(
                "https://1xxcc82bs9.execute-api.us-east-1.amazonaws.com/v1/api/book",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                        Authorization: `Bearer ${token}`,
                    },
                    body: JSON.stringify({ name, service, datetime: combinedDateTime }),
                }
            );

            const data = await res.json();

            if (!res.ok) {
                setMessage(data.error || "Booking failed");
            } else {
                setMessage(data.message);
                setName("");
                setService("");
                setDatetime("");
                setSelectedDate("");
                setSelectedTime("");
                // Set flag to show success message on booking requests page
                sessionStorage.setItem('justBooked', 'true');
                // Redirect to booking requests page after successful booking
                setTimeout(() => {
                    navigate("/requests");
                }, 1500);
            }
        } catch (error) {
            setMessage("Something went wrong. Please try again later.");
            console.error(error);
        } finally {
            setIsSubmitting(false);
        }
    };

        return (
            <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-slate-50 py-8 px-4">
                <div className="max-w-4xl mx-auto">
                    {/* Header */}
                    <div className="text-center mb-8">
                        <h1 className="text-4xl md:text-5xl font-black bg-gradient-to-r from-blue-900 via-blue-700 to-blue-400 bg-clip-text text-transparent mb-4">
                            Book Your Service
                        </h1>
                        <p className="text-lg text-slate-600 max-w-2xl mx-auto">
                            Choose from our professional services and schedule your appointment with ease
                        </p>
                    </div>

                    {/* Main Booking Card */}
                    <div className="bg-white rounded-3xl shadow-2xl border border-slate-100 overflow-hidden">
                        <form onSubmit={handleSubmit} className="p-8">
                            {/* Step 1: Service Selection */}
                            <div className="mb-8">
                                <h2 className="text-2xl font-bold text-slate-800 mb-6 flex items-center gap-3">
                                    <span className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center text-blue-600 font-bold">1</span>
                                    Choose Your Service
                                </h2>
                                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                                    {serviceOptions.map((option) => (
                                        <div
                                            key={option.id}
                                            onClick={() => setService(option.name)}
                                            className={`p-4 rounded-xl border-2 cursor-pointer transition-all duration-200 hover:shadow-lg ${
                                                service === option.name
                                                    ? 'border-blue-500 bg-blue-50 shadow-md'
                                                    : 'border-slate-200 hover:border-blue-300 bg-white'
                                            }`}
                                        >
                                            <div className="flex items-center gap-3">
                                                <span className="text-2xl">{option.icon}</span>
                                                <div>
                                                    <h3 className="font-semibold text-slate-800">{option.name}</h3>
                                                    <p className="text-sm text-slate-600">{option.description}</p>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>

                            {/* Step 2: Personal Details */}
                            <div className="mb-8">
                                <h2 className="text-2xl font-bold text-slate-800 mb-6 flex items-center gap-3">
                                    <span className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center text-blue-600 font-bold">2</span>
                                    Your Details
                                </h2>
                                <div className="max-w-md">
                                    <div className="mb-4">
                                        <label htmlFor="name" className="block text-sm font-semibold text-slate-700 mb-2">
                                            Full Name *
                                        </label>
                                        <input
                                            id="name"
                                            type="text"
                                            value={name}
                                            onChange={(e) => setName(e.target.value)}
                                            placeholder="Enter your full name"
                                            required
                                            className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition"
                                        />
                                    </div>
                                </div>
                            </div>

                            {/* Step 3: Schedule */}
                            <div className="mb-8">
                                <h2 className="text-2xl font-bold text-slate-800 mb-6 flex items-center gap-3">
                                    <span className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center text-blue-600 font-bold">3</span>
                                    Schedule Appointment
                                </h2>
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-md">
                                    <div>
                                        <label htmlFor="date" className="block text-sm font-semibold text-slate-700 mb-2">
                                            Preferred Date *
                                        </label>
                                        <input
                                            id="date"
                                            type="date"
                                            value={selectedDate}
                                            onChange={(e) => setSelectedDate(e.target.value)}
                                            min={new Date().toISOString().split('T')[0]}
                                            required
                                            className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition"
                                        />
                                    </div>
                                    <div>
                                        <label htmlFor="time" className="block text-sm font-semibold text-slate-700 mb-2">
                                            Preferred Time *
                                        </label>
                                        <select
                                            id="time"
                                            value={selectedTime}
                                            onChange={(e) => setSelectedTime(e.target.value)}
                                            required
                                            className="w-full px-4 py-3 border border-slate-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition"
                                        >
                                            <option value="">Select time</option>
                                            <option value="09:00">9:00 AM</option>
                                            <option value="10:00">10:00 AM</option>
                                            <option value="11:00">11:00 AM</option>
                                            <option value="12:00">12:00 PM</option>
                                            <option value="13:00">1:00 PM</option>
                                            <option value="14:00">2:00 PM</option>
                                            <option value="15:00">3:00 PM</option>
                                            <option value="16:00">4:00 PM</option>
                                            <option value="17:00">5:00 PM</option>
                                        </select>
                                    </div>
                                </div>
                            </div>

                            {/* Submit Button */}
                            <div className="flex justify-center">
                                <button
                                    type="submit"
                                    disabled={isSubmitting || !name || !service || (!selectedDate && !selectedTime)}
                                    className="px-8 py-4 bg-gradient-to-r from-blue-600 to-blue-700 text-white font-bold rounded-xl shadow-lg hover:from-blue-700 hover:to-blue-800 transition disabled:opacity-50 disabled:cursor-not-allowed text-lg flex items-center gap-3"
                                >
                                    {isSubmitting ? (
                                        <>
                                            <svg className="animate-spin h-5 w-5" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                                            </svg>
                                            Processing...
                                        </>
                                    ) : (
                                        <>
                                            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
                                            </svg>
                                            Book Service
                                        </>
                                    )}
                                </button>
                            </div>
                        </form>

                        {/* Success/Error Messages */}
                        {message && (
                            <div className="px-8 pb-8">
                                <div className={`p-4 rounded-xl border ${
                                    message.includes("successfully") 
                                        ? "bg-green-50 border-green-200 text-green-800" 
                                        : "bg-red-50 border-red-200 text-red-800"
                                }`}>
                                    <div className="flex items-center gap-3">
                                        {message.includes("successfully") ? (
                                            <svg className="w-5 h-5 text-green-600" fill="currentColor" viewBox="0 0 20 20">
                                                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                                            </svg>
                                        ) : (
                                            <svg className="w-5 h-5 text-red-600" fill="currentColor" viewBox="0 0 20 20">
                                                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7 4a1 1 0 11-2 0 1 1 0 012 0zm-1-9a1 1 0 00-1 1v4a1 1 0 102 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
                                            </svg>
                                        )}
                                        <div>
                                            <p className="font-semibold">{message}</p>
                                            {message.includes("successfully") && (
                                                <p className="text-sm opacity-80">Redirecting to your requests...</p>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        );
}
